import Vue from 'vue'
import Vuex from 'vuex'
import { fetchNewsList } from "../api/index.js";

Vue.use(Vuex)

// Vuex: 상태(여러 컴포넌트 끼리 공유되는 데이터 속성)관리 라이브러리,
export const store = new Vuex.Store({
  state: {
    news: []
  },
  actions: {
    FETCH_NEWS() {
      fetchNewsList()
        .then(response => {
          console.log(response)
        }).catch(error => {
          console.log(error)
        })
    }
  }
})
