import bus from "../utils/bus.js";

export default {
  // 재사용할 컴포넌트 옵션
  created() {
    bus.$emit("start:spinner");

    // 1. 데이터 호출 시점
    this.$store.dispatch("FETCH_LIST", this.$route.name)
      .then(() => {
        bus.$emit("end:spinner");
      })
      .catch((error) => {
        console.log(error);
      });
  }
}
