import { fetchJobsList } from "../api/index.js";
import ListItem from "../components/ListItem.vue";
import ListMixin from "../mixins/ListMixin.js";
import bus from "../utils/bus.js";
export default {
  components: {
    ListItem
  },
  mixins: [ListMixin],
  // -> ListMixin 내용이 그대로 들어감

  created: function () {
    fetchJobsList().then(response => this.jobs = response.data).catch(error => alert(error));
    bus.$emit("start:spinner"); // create되면 바로 스피너 호출
    setTimeout(() => {
      this.$store.dispatch("FETCH_JOBS").then(() => {
        // actions.js에 fetchNewsList반환값이 promise라서 then 게속 사용 가능
        bus.$emit("end:spinner");
      }).catch(error => {
        console.log(error);
      });
    }, 1000); // 타임아웃 걸어서 3초뒤에 받아옴
  }
};
