import Vue from 'vue'
import Vuex from 'vuex'
import { fetchNewsList } from "../api/index.js";

Vue.use(Vuex)

// Vuex: 상태(여러 컴포넌트 끼리 공유되는 데이터 속성)관리 라이브러리,
export const store = new Vuex.Store({
  state: {
    news: []
  },
  mutations: { // state에 접근하기 위해 사용
    SET_NEWS(state, news) {
      state.news = news;
    }
  },
  actions: {
    FETCH_NEWS(context) {
      fetchNewsList()
        .then(response => {
          console.log(response.data);
          // commit을 해야 mutation접근 가능
          context.commit('SET_NEWS', response.data);
        })
        .catch(error => {
          console.log(error)
        })
    }
  }
})
