import Vue from 'vue'
import Vuex from 'vuex'
import { fetchNewsList, fetchAskList, fetchJobsList } from "../api/index.js";

Vue.use(Vuex)

// Vuex: 상태(여러 컴포넌트 끼리 공유되는 데이터 속성)관리 라이브러리,
export const store = new Vuex.Store({
  state: {
    news: [],
    ask_items: [],
    jobs: []
  },
  mutations: { // state에 접근하기 위해 사용
    SET_NEWS(state, news) {
      state.news = news;
    },
    SET_ASK(state, ask_items) {
      state.ask_items = ask_items;
    },
    SET_JOBS(state, jobs) {
      state.jobs = jobs;
    }
  },
  actions: {
    FETCH_NEWS(context) {
      fetchNewsList()
        .then(response => {
          console.log(response.data);
          // commit을 해야 mutation접근 가능
          context.commit('SET_NEWS', response.data);
        })
        .catch(error => {
          console.log('NEWS...ERROR', error);
        })
    },
    // DATA Destructuring
    FETCH_ASK({ commit }) {
      fetchAskList()
        .then(({ data }) => {
          commit('SET_ASK', data);
        })
        .catch(error => {
          console.log('ASK...ERROR', error);
        });
    },
    FETCH_JOBS({ commit }) {
      fetchJobsList()
        .then(({ data }) => {
          commit('SET_JOBS', data);
        })
        .catch(error => {
          console.log('JOBS...ERROR', error);
        })
    }
  }
})
